import { useState, useEffect } from "react";
import { Item } from "./Item";

function ItemsList({ pokemon }) {
  const [pokemonItem, setPokemonItem] = useState([]);

    // console.log('pokemon', pokemon);
  useEffect(() => {
    let urls = pokemon.map((p) => `https://api.escuelajs.co/api/v1/products/${p}`);
    console.log('urls', urls);

    let fetches = urls.map((url) => fetch(url).then(response => response.json()));
    console.log('fetches', fetches);

    Promise.allSettled(fetches)
    .then((re))

    pokemon.forEach((p) =>
      fetch(`https://api.escuelajs.co/api/v1/products/${p}`)
        .then((response) => response.json())
        .then((data) => {
          // console.log("data", data);
          setPokemonItem((prevState) => [
            ...prevState,
            {
              id: data.id,
              name: data.title,
              images: data.images[0],
            },
          ]);
        })
    );
  }, [pokemon]);
  // console.log("pokemonItem", pokemonItem);
  // console.log("pokemon", pokemon);

  return (
    <div>
      {/* <p>{pokemonItem}</p> */}
      {pokemonItem.map((pokemon, index) => {
        return (
          <div key={pokemon.id}>
            <img src={pokemon.images} alt="" />
            <span className="card-title">{pokemon.name}</span>
            <hr />
          </div>
        );
      })}
    </div>
  );
}

export { ItemsList };
