import { useState, useEffect } from "react";
import {Item} from './Item'

function ItemsList({ pokemon }) {
  const [pokemonItem, setPokemonItem] = useState([]);

  useEffect(() => {
    pokemon.map((p) =>
      fetch(`https://pokeapi.co/api/v2/pokemon/${p}`)
        .then((response) => response.json())
        .then((character) => {
          setPokemonItem((prevState) => [
              ...prevState,
              {
              name: character.name,
              sprite: character.sprites.other["official-artwork"].front_default,
            },
          ]);
        })
    );
  }, [pokemon]);

  console.log("pokemonItem", pokemonItem);

  return (
    <>
    <div>
      {pokemon.map((p) => (
        <div key={p}>{p}</div>
      ))}
      <Item pokemonItem={pokemonItem}/>
    </div>
    </>
  );
}

export { ItemsList };
