import { useState, useEffect } from "react";
import {Item} from './Item'

function ItemsList({ pokemon }) {
  const [pokemonItem, setPokemonItem] = useState([]);



  useEffect(() => {
    pokemon.forEach((p) => console.log('fjhvf', `https://api.escuelajs.co/api/v1/products/${p}`));

      pokemon.forEach((p) =>
      fetch(`https://pokeapi.co/api/v2/pokemon/${p}`)
      .then((response) => response.json())
      .then((character) => {
        console.log("pokemon", pokemon);
          setPokemonItem((prevState) => [
              ...prevState,
              {
                  name: character.name,
                  sprite: character.sprites.other["official-artwork"].front_default,
                },
            ]);
        })
        );
    }, [pokemon]);
    // console.log("pokemonItem", pokemonItem);
    // console.log("pokemon", pokemon);


return (
    <div>
      {/* <p>{pokemonItem}</p> */}
      {pokemonItem.map((pokemon, index) => {
        return (
          <div key={pokemon.name}>
              <img src={pokemon.sprite} alt="" />
              <span className="card-title">{pokemon.name}</span>
            <hr />
          </div>
        );
      })}
    </div>
  );
}

export { ItemsList };
