import { useState, useEffect } from "react";

function ItemsList({ pokemon }) {
  const [pokemonItem, setPokemonItem] = useState([]);

  useEffect(() => {
    pokemon.map((p) =>
      fetch("https://pokeapi.co/api/v2/pokemon/${p}")
        .then((response) => response.json())
        .then((data) => {
          data.sprites && setPokemonItem(data.sprites.map((res) => res.name));
        })
    );
  }, []);

  console.log("pokemonItem", pokemonItem);

  return (
    <div>
      {pokemon.map((p) => (
        <div key={p}>{p}</div>
      ))}
    </div>
  );
}

export { ItemsList };
